mapper.select:
    	select  
    	from tk_home 
    	
    		
	    		hm_id = #{hmId, jdbcType=INTEGER}
	    	
	    	
	    		and hm_type = #{hmType, jdbcType=VARCHAR}
	    	
	    	
	    		and hm_state = #{hmState, jdbcType=INTEGER}
	    	
	    	
	    		and hm_state <> -1
	    	
    	
    	order by hm_seq
